package javadouble.practice;

public class MinMax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		j. Declare two double variables with values 112.2 and 556.6, and find the minimum
//		and maximum values using the Double class. (Hint: Use Double.min(double,
//		double) and Double.max(double, double))
		double num1 = 112.2;
		double num2 = 556.6;
		System.out.println("Max is " +Double.max(num1,num2));
		System.out.println("Min is " +Double.min(num1,num2));
		

	}

}
