package JavaFloat;

public class AdditionFloat {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		. Declare two float variables with values 112.3 and 984.5, and add them using a
//		method from the Float class. (Hint: Use Float.sum(float, float)).
		float num1 = 112.3f;
		float num2 = 984.5f;
		float addition = Float.sum(num1, num2);
		System.out.println("The addition is :"+addition);
	}

}
