package JavaFloat;

public class MinMax {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		j. Declare two float variables with values 112.2 and 556.6, and find the minimum
//		and maximum values using the Float class. (Hint: Use Float.min(float, float)
//		and Float.max(float, float)).
		float num1 = 112.2f;
		float num2 = 556.6f;
	System.out.println("The minimum is:" +Float.min(num1,num2));
	System.out.println("The maximum is:" +Float.max(num1,num2));

	
				

	}

}
