package question4;

public class CompareInt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		j. Declare two integer variables with values 10 and 20, and find the minimum and
//		maximum values using the Integer class. (Hint: Use Integer.min(int, int) and
//		Integer.max(int, int)
		int val1 = 10;
		int val2 = 20;
		int min = Integer.min(val1,val2);
		int max = Integer.max(val1,val2);
		System.out.println("Minimum is: "+min);
		System.out.println("Maximum is: "+max);


	}

}
