package javalong;

public class LongMaxMin {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
//		j. Declare two long variables with values 1122 and 5566, and find the minimum and
//		maximum values using the Long class. (Hint: Use Long.min(long, long) and
//		Long.max(long, long)).
		long val1 = 1122;
		long val2 = 5566;
		System.out.println("Min is: "+Long.min(val1,val2));
		System.out.println("Max is: "+Long.max(val1,val2));


	}

}
