package Short;

public class Experiment {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		short Num = 100;

        // 1. Convert short to int
        int intNum = Num;
        System.out.println("Short to int: " + intNum);

        // 2. Convert short to long
        long longNum = Num;
        System.out.println("Short to long: " + longNum);

      //3. Convert short to float
        float floatNum = Num;
        System.out.println("Short to float: " + floatNum);


        // 6. Convert short to char 
       char charNum = (char) Num;
        System.out.println("Short to char: " + (int) charNum);  

	}

}
